mapper.select:
SELECT uid, tenant_id AS tenantId, nickname, status, deleted, version, password_hash AS passwordHash, updated_at AS updatedAt
FROM ${table}
WHERE tenant_id = #{tenantId} AND uid = #{uid}
LIMIT 1

SELECT uid, tenant_id AS tenantId, phone, email, nickname, gender, status, deleted, version, created_at AS createdAt, updated_at AS updatedAt
FROM ${table}
WHERE tenant_id = #{tenantId} AND uid = #{uid}
LIMIT 1

mapper.update:
UPDATE ${table}
SET nickname=#{nickname}, gender=#{gender}, status=#{status}, deleted=#{deleted},
    version = version + 1,
    updated_at = NOW()
WHERE tenant_id = #{tenantId} AND uid = #{uid}
